mapper.select:
select 

from device_medicine_maintance
where id = #{id,jdbcType=BIGINT}

select * from device_medicine_maintance left join device on device_medicine_maintance.device_id = device.id where CustomProject = #{customProject}



      and device.CustomSerial = #{searchText}


      and device.CustomTown = #{searchText}


      and batch = #{searchText}


      and Worker = #{searchText}


      and concat(device_Id,CustomTown,batch,Worker) like concat('%',#{searchText},'%')




  and submit_date >= #{startDate}


  and submit_date <= #{endDate}

select * from device_medicine_maintance left join device on device_id = device.id where adcode like concat('%' ,#{adcode}, '%')
and isManagerAssign = 1



      and device.CustomSerial = #{searchText}


      and device.CustomTown = #{searchText}


      and batch = #{searchText}


      and Worker =  #{searchText}


      and concat(device_Id,CustomTown,batch,Worker) like concat('%',#{searchText},'%')




  and submit_date >= #{startDate}


  and submit_date <= #{endDate}

select * from device_medicine_maintance left join device on device_medicine_maintance.device_id = device.id
WHERE device.adcode
LIKE concat(#{adcode}, '%')

  and device_medicine_maintance.submit_date >= #{startDate}


  and device_medicine_maintance.submit_date <= #{endDate}


  and ${colName} = #{searchText}

select device.CustomTown,device_medicine_maintance.*,sum(device_medicine_maintance.medicine_qua) medicineQuaSum,
sum(device_medicine_maintance.area_fz) areaFzSum
from device left join device_medicine_maintance on device.id = device_medicine_maintance.device_id

  and submit_date >= #{startDate}


  and submit_date <= #{endDate}

group by device.CustomTown

select t2.CustomTown,t1.*,sum(t1.medicine_qua) medicineQuaSum,
sum(t1.area_fz) areaFzSum
from device_medicine_maintance t1 left join device t2 on t1.device_id = t2.id
where t2.CustomProject = #{customProject}

  and t1.submit_date >= #{startDate}


  and t1.submit_date <= #{endDate}

group by t2.CustomTown

select device_medicine_maintance.id, device_medicine_maintance.device_id, device_medicine_maintance.batch, device_medicine_maintance.medicine_qua, device_medicine_maintance.username, user.name, device_medicine_maintance.submit_date, device_medicine_maintance.longitude, device_medicine_maintance.latitude, device_medicine_maintance.altitude, device_medicine_maintance.pic, device_medicine_maintance.medicine_name, device_medicine_maintance.area_fz, device_medicine_maintance.scanId, device_medicine_maintance.remarks, device.province, device.city, device.area, device_medicine_maintance.town, device_medicine_maintance.work_content, device_medicine_maintance.reported from device_medicine_maintance left join device on device.id = device_medicine_maintance.device_id left join user on device_medicine_maintance.username = user.username where device_id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username like concat('%', #{condition}, '%') or device.province like concat('%', #{condition}, '%') or device.city like concat('%', #{condition}, '%') or device.area like concat('%', #{condition}, '%') or device.town like concat('%', #{condition}, '%') or user.username like concat('%', #{condition}, '%') or user.name like concat('%', #{condition}, '%') or device_medicine_maintance.device_id like concat('%', #{condition}, '%') )
and `batch` = #{batch}
and submit_date >= #{date}
and device.town like concat('%', #{searchtown}, '%')
and submit_date <= #{endDate}
and reported = #{reported}
order by submit_date desc

select device_medicine_maintance.id, device_medicine_maintance.device_Id, device_medicine_maintance.batch, device_medicine_maintance.medicine_qua, device_medicine_maintance.username, user.name, device_medicine_maintance.submit_date, device_medicine_maintance.longitude, device_medicine_maintance.latitude, device_medicine_maintance.altitude, device_medicine_maintance.pic, device_medicine_maintance.medicine_name, device_medicine_maintance.area_fz, device_medicine_maintance.scanId, device_medicine_maintance.remarks, device.province, device.city, device.area, device_medicine_maintance.town, device_medicine_maintance.work_content, device_medicine_maintance.reported from device_medicine_maintance left join device on device.Id = device_medicine_maintance.device_id left join user on device_medicine_maintance.username = user.username where device_id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username like concat('%', #{condition}, '%') or device.province like concat('%', #{condition}, '%') or device.city like concat('%', #{condition}, '%') or device.area like concat('%', #{condition}, '%') or device.town like concat('%', #{condition}, '%') or user.username like concat('%', #{condition}, '%') or user.name like concat('%', #{condition}, '%') or device_medicine_maintance.device_id like concat('%', #{condition}, '%') )
and submit_date >= #{date}
and `batch` = #{batch}
and device.town like concat('%', #{searchtown}, '%')
and submit_date <= #{endDate}
and manager = #{manager}
order by submit_date desc

select count(distinct device_id) deviceCount, sum(injection_num) injectNum, device.area `name`, device.adcode code from Device_Injection_maintance left join device on Device_Injection_maintance.device_id = device.id where adcode like concat(#{adcode}, '%')
and device.area is not null
and submit_date >= #{startDate}
and submit_date <= #{endDate}
group by device.area

select count(distinct device_id) `count`, sum(medicine_qua) medicineQuaSum,sum(area_fz) areaFzSum
from device_medicine_maintance
left join device on device.id = device_medicine_maintance.device_id
where adcode like concat(#{adcode}, '%')



  and submit_date >= #{startDate}




  and submit_date <= #{endDate}

select count(distinct device_id) `count`, sum(medicine_qua) medicineQuaSum,sum(area_fz) areaFzSum from device_medicine_maintance
left join device on device.id = device_medicine_maintance.device_id
left join user on device_medicine_maintance.username = user.username
where parent = #{adcode}


  and submit_date >= #{startDate}


  and submit_date <= #{endDate}

select count(distinct device_id) deviceCount, sum(medicine_qua) medicineQuaSum,sum(area_fz) areaFzSum , device.manager `name`, device.adcode code from device_medicine_maintance left join device on device_medicine_maintance.device_id = device.id where device.adcode like concat(#{adcode}, '%')
and device.manager is not null and device.project = '药剂防治管理'
and submit_date >= #{startDate}
and submit_date <= #{endDate}
group by device.manager

select count(distinct device_id) deviceCount, sum(medicine_qua) medicineQuaSum, sum(area_fz) areaFzSum,device_medicine_maintance.town `name`, device.adcode code from device_medicine_maintance left join device on device_medicine_maintance.device_id = device.id where adcode like concat(#{adcode}, '%') and device_medicine_maintance.town is not null
and submit_Date >= #{startDate}
and submit_Date <= #{endDate}
group by device_medicine_maintance.town

select device_medicine_maintance.id, device_medicine_maintance.device_id, device_medicine_maintance.batch, sum(device_medicine_maintance.medicine_qua) medicineQua, device_medicine_maintance.worker, user.name, device_medicine_maintance.submit_date, device_medicine_maintance.longitude, device_medicine_maintance.latitude, device_medicine_maintance.altitude, device_medicine_maintance.pic, device_medicine_maintance.remarks, device_medicine_maintance.reported, device.province, device.city, device.area, device_medicine_maintance.town, device_medicine_maintance.work_content, device_medicine_maintance.username, device_medicine_maintance.medicine_name, device_medicine_maintance.area_fz from device_medicine_maintance left join device on device.id = device_medicine_maintance.device_id left join user on device_medicine_maintance.worker = user.username where device_id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and device_medicine_maintance.town = #{town}
)
and ( user.username = #{condition} or device.province = #{condition} or device.city = #{condition} or device.area = #{condition} or device_medicine_maintance.town = #{condition} or device_medicine_maintance.device_id = #{condition} or device.manager=#{condition} or device.CustomProject = #{condition} )
and submit_date >= #{date}
and submit_date <= #{endDate}
group by device_id,device_medicine_maintance.worker
order by submit_date desc

select device_medicine_maintance.id, device_medicine_maintance.device_id, device_medicine_maintance.batch, sum(device_medicine_maintance.medicine_qua) medicineQua, device_medicine_maintance.worker, user.name, device_medicine_maintance.submit_date, device_medicine_maintance.longitude, device_medicine_maintance.latitude, device_medicine_maintance.altitude, device_medicine_maintance.pic, device_medicine_maintance.eemarks, device_medicine_maintance.reported, device.province, device.city, device.area, device.town, device_medicine_maintance.work_content, device_medicine_maintance.medicine_name, device_medicine_maintance.area_fz, device_medicine_maintance.username from device_medicine_maintance left join device on device.id = device_medicine_maintance.device_id left join user on device_medicine_maintance.worker = user.username where device_id in (select id from device where 1 = 1
and adcode like concat(#{adcode}, '%')
and town = #{town}
)
and ( user.username =#{condition} or device.province =#{condition} or device.city = #{condition} or device.area =#{condition} or device.town = #{condition} or device_medicine_maintance.device_id = #{condition} or device.CustomProject = #{condition} )
and submit_date >= #{date}
and submit_date <= #{endDate}
and manager = #{manager}
group by device_id,device_medicine_maintance.worker order by submit_date desc

