submit_date=#{submitDate},batch=#{batch},longitude=#{longitude},latitude=#{latitude},work_content=#{workContent},
medicine_qua=#{medicineQua},medicine_name = #{medicineName},worker=#{worker},remarks=#{remarks},area_fz=#{areaFz},altitude=#{altitude} where id=#{id};

